import React from "react";
import MessageNoPassword from './Password/MessageNoPassword'
import MessageShortPassword from './Password/MessageShortPassword'

function Password(props) {
  console.log(props);
  let passNotSpec
  let passIsShorter
  if (props.value==="") {passNotSpec=<MessageNoPassword/>}
  if (props.value.length<5 && props.value.length>=1) {passIsShorter=<MessageShortPassword/>}
  return (
    <div className="page__password">
        <div>
          Пароль
        </div>
        <div>
          <input type="password" name="firstPassword" value={props.value} onChange={props.onChange} formAction={props.indicatorPlus(props.indicator)}/>
        </div>
        {passNotSpec}
        {passIsShorter}
    </div>
  )
}

export default Password
