import React from "react";

import Status from "./Forms/Status";
import SortMas from "./Forms/SortMas"
import Password from "./Forms/Password";
import PasswordRepeat from "./Forms/PasswordRepeat";
import Mail from "./Forms/Mail";
import CheckBox from "./Forms/CheckBox";
import ButtonCheck from "./Forms/ButtonCheck";


let cities = SortMas()

class PasswordMail extends React.Component {
    state = {
    password: "",
    passwordRepeat: "",
    email: "",
    agreement: false,
    city: cities[0],
    buttons: false,
    status: "Прежде чем действовать, надо понять",
    checkPass: false,
    indicator: 0
  };

  IndicatorPlus = field => event => {
    this.setState({ [field] : this.state[field]+1});
  }

  handleButtonPlus = field => event => {
    this.setState({ [field] : true});
  }

  handleButtonMinus = field => event => {
    this.setState({ [field] : false});
  }

  handleCityChange = event => {
    this.setState({ city: cities[event.target.value] });
  }

  handleTextInputChange = field => event => {
    this.setState({ [field]: event.target.value });
  }

  handleBooleanInputChange = field => event => {
    this.setState({ [field]: event.target.checked });
  }

  render() {
    return (
      <form className="page">
        <Status onClick1={this.handleButtonPlus("buttons")} onClick2={this.handleButtonMinus("buttons")} button={this.state.buttons} status={this.state.status} onChange={this.handleTextInputChange("status")}/>
        <div className="page__citySelection">
          <div className="page__citySelection__heading">
            Ваш город
          </div>
          <div className="page__citySelection__bodySelect">
            <select className="page__citySelection__bodySelect--citySelection" onChange={this.handleCityChange}>{cities.map((city, index) => (<option value={index} key={city.city}>{city.city}</option>))}</select>
          </div>
        </div>
        <hr/>
        <Password indicatorPlus={this.IndicatorPlus("indicator")} indicator={this.state.indicator} value={this.state.password} onChange={this.handleTextInputChange("password")}/>
        <PasswordRepeat value1={this.state.password} value2={this.state.passwordRepeat} onChange={this.handleTextInputChange("passwordRepeat")}/>
        <hr className="page__line2"/>
        <Mail value={this.state.email} onChange={this.handleTextInputChange("email")}/>
        <CheckBox checked={this.state.agreement} onChange={this.handleBooleanInputChange("agreement")}/>
        <ButtonCheck checkPass={this.state.checkPass} password={this.state.password} passwordRepeat={this.state.passwordRepeat} email={this.state.email} onClick1={this.handleButtonMinus("checkPass")} onClick2={this.handleButtonPlus("checkPass")}/>
      </form>
    );
  }
}

export default PasswordMail;
