import React from "react";
import MessageNoMail from './Mail/MessageNoMail'
import MessageIncorrectMail from './Mail/MessageIncorrectMail'

function Mail(props) {
  let mailNotSpec
  let mailIncorrect
  let validationCheck
  let mailValid = /^([\w.%+-]+)@([\w-]+\.)+([\w]{2,})$/i.test(props.value)
  if (props.value==="") {mailNotSpec=<MessageNoMail/>; validationCheck="page__mail__body--notValidMail"} else {validationCheck="page__mail__body--validMail"}
  if (mailValid!==true && props.value.length>=1) {mailIncorrect=<MessageIncorrectMail/>}
  return (
    <div className="page__mail">
      <div className="page__mail__heading">
        Электронная почта
      </div>
      <div className="page__mail__body">
        <input className={validationCheck} type="text" name="mail" value={props.value} onChange={props.onChange}/>
      </div>
      {mailNotSpec}
      {mailIncorrect}
    </div>
  )
}

export default Mail
