import React from "react";

function Password(props) {
  let fff="";
  if (props.value==="") {fff="INPUTPMWARN"} else {fff="INPUTPM"}
  return (
    <div className="TEXT">
      <div>
        Пароль {" "}
      </div>
      <div>
        <input className={fff} type="password" name="firstPassword" value={props.value} onChange={props.onChange}/>
      </div>
    </div>
  )
}

export default Password
