import React from "react";

function CheckAll (props) {
console.log(props)
let indicator=0;
let emailValid = false
let buttonFinish
emailValid = /^([\w.%+-]+)@([\w-]+\.)+([\w]{2,})$/i.test(props.email)
if (props.password!=="") {indicator = indicator + 1}
if (props.password.length>=5) {indicator = indicator + 1}
if (props.passwordRepeat!=="") {indicator = indicator + 1}
if (props.password===props.passwordRepeat && props.passwordRepeat.length>=1) {indicator = indicator + 1}
if (props.email!=="") {indicator = indicator + 1}
if (emailValid===true && props.email.length>=1) {indicator = indicator + 1}
if (indicator!==6) {
  buttonFinish=<button type="button" onClick={props.onClick}>Изменить</button>
}
  return (
    <div className="page__button">
      {buttonFinish}
    </div>
  )
}

export default CheckAll
