import React from "react";
import MessageNoMail from './Mail/MessageNoMail'
import MessageIncorrectName from './Mail/MessageIncorrectName'

function Mail(props) {
  let mailNotSpec
  let mailIncorrect
  let mailValid = /^([\w.%+-]+)@([\w-]+\.)+([\w]{2,})$/i.test(props.value)
  if (props.value==="") {mailNotSpec=<MessageNoMail/>}
  if (mailValid!==true && props.value.length>=1) {mailIncorrect=<MessageIncorrectName/>}
  return (
    <div className="page__mail">
      <div>
        Электронная почта
      </div>
      <div>
        <input type="text" name="mail" value={props.value} onChange={props.onChange}/>
      </div>
      {mailNotSpec}
      {mailIncorrect}
    </div>
  )
}

export default Mail
