import React from "react";

function PasswordRepeat(props) {
  let fff="";
  if (props.value==="") {fff="INPUTPMWARN"} else {fff="INPUTPM"}
  return (
    <div className="TEXT">
      <div>
        Пароль еще раз {" "}
      </div>
      <div>
        <input className={fff} type="password" placeholder="Пароль" name="twoPassword" value={props.value} onChange={props.onChange}/>
      </div>
    </div>
  )
}

export default PasswordRepeat
