import React from "react";
import SetPlus from "./Status/StatusRedact";
import SetMinus from "./Status/StatusStatic";

import CheckAll from "./Check/CheckAll";
import ShowTimes from "./Check/ShowTimes";

import Password from "./Forms/Password";
import PasswordRepeat from "./Forms/PasswordRepeat";
import Mail from "./Forms/Mail";
import CheckBox from "./Forms/CheckBox";
import SelectMas from "./Forms/SelectMas"


let cities = SelectMas()

class PasswordMail extends React.Component {
    state = {
    password: "",
    passwordRepeat: "",
    email: "",
    agreement: false,
    city: cities[0],
    buttons: false,
    status: "Прежде чем действовать, надо понять",
    checkPass: false,
    indicator: 0
  };



  handleButtonPlus = field => event => {
    this.setState({ [field] : true});
  }

  handleButtonMinus = field => event => {
    this.setState({ [field] : false});
  }

  handleCityChange = event => {
    this.setState({ city: cities[event.target.value] });
  }

  handleTextInputChange = field => event => {
    this.setState({ [field]: event.target.value });
  }

  handleBooleanInputChange = field => event => {
    this.setState({ [field]: event.target.checked });
  }

  render() {
    const buttonValue=this.state.buttons
    let formStatus = null
    const password1 = this.state.password
    const password2 = this.state.passwordRepeat
    const mail = this.state.email
    let buttonAll = null;
    let buttonTime = null;
    let mailValid = false
    mailValid = /^([\w.%+-]+)@([\w-]+\.)+([\w]{2,})$/i.test(mail)
    let valid = 0;
    if (password1!=="") {valid = valid + 1}
    if (password1.length>=5) {valid = valid + 1}
    if (password2!=="") {valid = valid + 1}
    if (password1===password2) {valid = valid + 1}
    if (mail!=="") {valid = valid + 1}
    if (mailValid===true) {valid = valid + 1}



    if (valid === 6) {
      buttonAll=<CheckAll onClick={this.handleButtonPlus("checkPass")}/>;
      if (this.state.checkPass) {
          buttonTime=<ShowTimes />
          console.log(JSON.stringify(this.state))}
    }
      else {
        buttonAll=<CheckAll onClick={this.handleButtonMinus("checkPass")}/>
      }
    return (
      <form className="page">
        <SetPlus onClick1={this.handleButtonPlus("buttons")} onClick2={this.handleButtonMinus("buttons")} button={this.state.buttons} status={this.state.status} onChange={this.handleTextInputChange("status")}/>
        <div className="page__citySelection">
          <div className="page__citySelection__heading">
            Ваш город
          </div>
          <div className="page__citySelection__bodySelect">
            <select className="page__citySelection__bodySelect--citySelection" onChange={this.handleCityChange}>{cities.map((city, index) => (<option value={index} key={city.city}>{city.city}</option>))}</select>
          </div>
        </div>
        <Password value={this.state.password} onChange={this.handleTextInputChange("password")}/>
        <PasswordRepeat value1={this.state.password} value2={this.state.passwordRepeat} onChange={this.handleTextInputChange("passwordRepeat")}/>
        <Mail value={this.state.email} onChange={this.handleTextInputChange("email")}/>
        <CheckBox checked={this.state.agreement} onChange={this.handleBooleanInputChange("agreement")}/>
          {buttonAll}
          {buttonTime}
      </form>
    );
  }
}

export default PasswordMail;
