import React from "react";
import MessageNoPasswordRepeat from './PasswordRepeat/MessageNoPasswordRepeat'
import MessageDifferentPassword from './PasswordRepeat/MessageDifferentPassword'

function PasswordRepeat(props) {
  let passNotSpec
  let passDifferent
  let validationCheck
  if (props.value2==="") {passNotSpec=<MessageNoPasswordRepeat/>; validationCheck="page__passwordRepeat__body--notValidPasswordRepeat"} else {validationCheck="page__passwordRepeat__body--validPasswordRepeat"}
  if (props.value1!==props.value2 && props.value2.length>1) {passDifferent=<MessageDifferentPassword/>}
  return (
    <div className="page__passwordRepeat">
      <div className="page__passwordRepeat__heading">
        Пароль еще раз
      </div>
      <div className="page__passwordRepeat__body">
        <input className={validationCheck} type="password" name="twoPassword" value2={props.value2} onChange={props.onChange}/>
      </div>
      {passNotSpec}
      {passDifferent}
    </div>
  )
}

export default PasswordRepeat
