import React from "react";
import MessageNoPasswordRepeat from './Password/MessageNoPasswordRepeat'

function PasswordRepeat(props) {
  let passNotSpec
  if (props.value==="") {passNotSpec=<MessageNoPasswordRepeat/>}
  return (
    <div className="page__passwordRepeat">
      <div>
        Пароль еще раз
      </div>
      <div>
        <input type="password" name="twoPassword" value={props.value} onChange={props.onChange}/>
      </div>
    </div>
  )
}

export default PasswordRepeat
