import React from "react";
import MessageNoPassword from './Password/MessageNoPassword'

function Password(props) {
  let stylePasswordLength="";
  let passNotSpec;
  let passIsShorter;
  if (props.value==="") {stylePasswordLength="page__password__bodyInput--unsuccessfully"; passNotSpec=<MessageNoPassword/>}
  else {stylePasswordLength="page__password__bodyInput--well"}
  return (
    <div className="page__password">
        <div className="page__password__heading">
          Пароль
        </div>
        <div className="page__password__bodyInput">
          <input className={stylePasswordLength} type="password" name="firstPassword" value={props.value} onChange={props.onChange}/>
        </div>
        {passNotSpec}
    </div>
  )
}

export default Password
