import React from 'react'
import './App.css'

import Header from './Block/Header'
import StatusLine from './Block/StatusLine'


class App extends React.Component {
  state = {
    indicatorStatus: false,
    valueStatus: "",
    valuePassword: "",
    valueEmail: ""
  }
  changeIndicator = (field) => event => {
    this.setState({ [field]: !event.target.checked });
  }
  textInputChange = (field) => event => {
    this.setState({ [field]: event.target.value });
  }
  render() {
    return (
      <div className="wrapper" >
        <Header indicatorStatus={this.state.indicatorStatus} changeIndicatorStatus={this.changeIndicator("indicatorStatus")}/>
        <StatusLine valueStatus={this.state.valueStatus} changeValueStatus={this.textInputChange("valueStatus")}/>
        <div>Город</div>
        <div>Пароль</div>
        <div>Логин</div>
        <div>Чекбокс</div>
        <div>Кнопка</div>
      </div>
    )
  }
}

export default App
