import React from "react";
import MessageNoPassword from './Password/MessageNoPassword'
import MessageShortPassword from './Password/MessageShortPassword'

function Password(props) {
  let passNotSpec
  let passIsShorter
  let validationCheck
  if (props.value==="") {passNotSpec=<MessageNoPassword/>; validationCheck="page__password__body--notValidPassword"} else {validationCheck="page__password__body--validPassword"}
  if (props.value.length<5 && props.value.length>=1) {passIsShorter=<MessageShortPassword/>}
  return (
    <div className="page__password">
        <div className="page__password__heading">
          Пароль
        </div>
        <div className="page__password__body">
          <input className={validationCheck} type="password" name="password" value={props.value} onChange={props.onChange}/>
        </div>
        {passNotSpec}
        {passIsShorter}
    </div>
  )
}

export default Password
