import React from 'react';

function Password (props) {
  return (
    <div className="CityPasMail">
      <div className="headingLayout">Пароль</div>
      <div className="menuLayout">
        <input className="inputText" type="text" value={props.valuePassword} onChange={props.changeValuePassword}>
        </input>
      </div>
      {props.valuePassword.length === 0 &&
        <div className="warning">
          Ваш новый пароль должен содержать не менее 5 символов
        </div>
      }
      <div className="outputWarning">
      
      </div>
    </div>
  )
}

export default Password
